next.when:
{{-
    w
    for w in CTX.sanity_check|d([])
    if w.status|d == "running"
    and w.id|d != CTX.execution_id|d
-}}

publish.value:
{{- [
    {
    "id" : work.id,
    "updated_at" : work.updatedAt,
    "updated_by" : work.updatedBy.username|d("ROC"),
    "created_at" : work.createdAt
    }
    for work in CTX.workflows
] -}}

{% set output = [] %}
{% for work in CTX.updated_at %}
{% set check = [v.updated_at for v in CTX.var if v.id == work.id] | first or false %}
{% do output.append({
    "id" : work.id,
    "isUpdated" : 0 if work.updated_at == check else 1,
    "updatedAt" : ((work.updated_at | int / 1000) | convert_from_epoch),
    "createdAt" : ((work.created_at | int / 1000) | convert_from_epoch),
    "updatedBy" : work.updated_by
}) %}
{% endfor %}
{{ output }}

{% set output = [] %}
{% for work in CTX.updated_at %}
{% do output.append({
    "id" : work.id,
    "isUpdated" : 0,
    "updatedAt" : ((work.updated_at | int / 1000) | convert_from_epoch),
    "createdAt" : ((work.created_at | int / 1000) | convert_from_epoch),
    "updatedBy" : work.updated_by
}) %}
{% endfor %}
{{ output }}

{%- set output = [] -%}
{%- for wrkflw_ex in CTX.workflow_executions %}
{#- Workflow -#}
{%- set workflow_object = [w for w in CTX.workflows if w.id == wrkflw_ex.workflow.id] | first|d(false) -%}
{%- set crate_name = workflow_object.unpackedFrom.name|d('Custom') -%}
{%- set updated_by = workflow_object.updatedBy.username|d('none') | lower -%}
{%- set timeout = workflow_object.timeout|d(0) | string -%}
{%- set type = workflow_object.type|d('none') -%}
{%- set created_by = workflow_object.createdBy.username|d('none') | lower -%}
{%- set seconds_saved = workflow_object.humanSecondsSaved|d(0) | string -%}
{%- set is_sync = "true" if workflow_object.isSynchronized|d(true) == true else "false" -%}
{%- set tags = [t.name for t in workflow_object.tags|d([{"name" : 'none'}])] | join(' ')|d('none') -%}
{#- Execution -#}
{%- set results_url = ("https://app.rewst.io/organizations/{}/results/{}".format(wrkflw_ex.orgId, wrkflw_ex.id)) -%}
{%- set workflow_url = ("https://app.rewst.io/organizations/{}/workflows/{}".format(wrkflw_ex.workflow.orgId, wrkflw_ex.workflow.id)) -%}
{%- set org_name = [o.name for o in CTX.orgs if wrkflw_ex.orgId == o.id] | first or ORG.ATTRIBUTES.name -%}
{%- set syslog_execution_url = "https://app.rewst.io/organizations/{}/results/{}".format(ORG.ATTRIBUTES.id, CTX.execution_id) -%}
{%- do output.append(({
    "msg" : ("Rewst_Workflow_Execution\n\nexecution_status = {}\n\norg_name = {}\n\nworkflow_name = {}\n\nexecution_id = {}\n\nworkflow_id = {}\n\norg_id = {}\n\nworkflow_org_id = {}\n\ncrate_name = {}\n\nexecution_url = {}\n\nworkflow_url = {}\n\nupdated_by = {}\n\ntimeout = {}\n\ntype = {}\n\nseconds_saved = {}\n\ncreated_by = {}\n\nis_sync = {}\n\ntags = {}\n\n".format(wrkflw_ex.status, org_name, wrkflw_ex.workflow.name, wrkflw_ex.id, wrkflw_ex.workflow.id, wrkflw_ex.orgId, wrkflw_ex.workflow.orgId, crate_name, results_url, workflow_url, updated_by, timeout, type, seconds_saved, created_by, is_sync, tags)),
    "execution_status" : wrkflw_ex.status,
    "workflow_id" : wrkflw_ex.workflow.id,
    "workflow_name" : wrkflw_ex.workflow.name,
    "updated_by" : updated_by,
    "org_id" : wrkflw_ex.orgId,
    "org_name" : org_name,
    "is_sync" : is_sync,
    "execution_id" : wrkflw_ex.id,
    "execution_url" : results_url,
    "workflow_url" : workflow_url,
    "tags" : tags,
    "crate_name" : crate_name,
    "updated_at" : (((wrkflw_ex.updatedAt | int) / 1000) | convert_from_epoch | format_datetime("%Y-%m-%dT%H:%M:%S.%f%z"))|d(CTX.time_stamp_pretty),
    "created_at" : (((wrkflw_ex.createdAt| int) / 1000) | convert_from_epoch | format_datetime("%Y-%m-%dT%H:%M:%S.%f%z"))|d(CTX.time_stamp_pretty),
    "syslog_identifier" : "Rewst_Workflow_Execution",
    "_timestamp" : CTX.time_stamp_pretty if ((wrkflw_ex.createdAt | int) / 1000) < (CTX.time_stamp - 120) else ((wrkflw_ex.createdAt | int) / 1000) | convert_from_epoch | format_datetime("%Y-%m-%dT%H:%M:%S.%f%z"),
    "_lm.resourceId" : {
        "system.deviceId" : ORG.VARIABLES.logicmonitor_datasource_device_id
    }
})) -%}
{%- endfor -%}
{{- output | batch(20) -}}

